import sys
from collections import defaultdict

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, K = int(data[0]), int(data[1])
    edges = defaultdict(list)
    for i in range(N - 1):
        u, v = map(int, data[2 * i + 1:2 * i + 3])
        edges[u].append(v)
        edges[v].append(u)
    return N, K, edges

def is_decomposable(N, K, edges):
    # Check if the graph is connected
    visited = [False] * (N * K + 1)
    def dfs(node):
        visited[node] = True
        for neighbor in edges[node]:
            if not visited[neighbor]:
                dfs(neighbor)
    dfs(1)
    if not all(visited):
        return False

    # Check if the graph is a tree
    parent = [-1] * (N * K + 1)
    def find(x):
        if parent[x] == -1:
            return x
        parent[x] = find(parent[x])
        return parent[x]
    for u, v in edges.items():
        pu, pv = find(u), find(v)
        if pu == pv:
            return False
        parent[pv] = pu

    # Check if the graph can be decomposed into N paths each of length K
    path_count = 0
    for i in range(1, N * K + 1):
        if not visited[i]:
            path_count += 1
            visited[i] = True
            current = i
            while current != 1:
                current = edges[current][0]
                visited[current] = True
    return path_count == N

def main():
    N, K, edges = read_input()
    if is_decomposable(N, K, edges):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()
