import sys
from collections import defaultdict

def is_odd_cycle(graph):
    visited = [False] * (n + 1)
    parent = [-1] * (n + 1)
    stack = []

    def dfs(node):
        visited[node] = True
        stack.append(node)

        for neighbor in graph[node]:
            if not visited[neighbor]:
                parent[neighbor] = node
                if dfs(neighbor):
                    return True
            elif parent[node] != neighbor:
                return True

