def sum_of_squares_modulo(n, k, A):
    total_sum = 0
    for i in range(n):
        total_sum += A[i]**2
    total_sum %= 998244353
    for i in range(n):
        for j in range(i+1, n):
            total_sum += A[i]*A[j]
            total_sum %= 998244353
    for i in range(n):
        for j in range(i+1, n):
            for k in range(j+1, n):
                total_sum += A[i]*A[j]*A[k]
                total_sum %= 998244353
    return total_sum**k % 998244353

# Read input
n, k = map(int, input().split())
A = list(map(int, input().split()))

