    # Function to check if a cell is a road
    def is_road(x, y):
        return grid[x-1][y-1] == '.'

    # Function to check if a cell is a wall
    def is_wall(x, y):
        return grid[x-1][y-1] == '#'

    # Function to perform a front kick
    def perform_front_kick(x, y, direction):
        if direction == 'up':
            for i in range(x-1, 0, -1):
                if is_road(i, y):
                    grid[i-1][y] = '.'
                    break
        elif direction == 'down':
            for i in range(x, H+1):
                if is_road(i, y):
                    grid[i-1][y] = '.'
                    break
        elif direction == 'left':
            for j in range(y-1, 0, -1):
                if is_road(x, j):
                    grid[x-1][j] = '.'
                    break
        elif direction == 'right':
            for j in range(y, W+1):
                if is_road(x, j):
                    grid[x-1][j] = '.'
                    break

    # Function to find the minimum number of front kicks
    def find_min_front_kicks(x, y):
        if x == C and y == D:
            return 0
        min_kicks = float('inf')
        for direction in ['up', 'down', 'left', 'right']:
            if is_within_grid(x, y) and is_road(x, y):
                perform_front_kick(x, y, direction)
                min_kicks = min(min_kicks, find_min_front_kicks(x+1, y) + 1)
                perform_front_kick(x-1, y, direction)
