        return 1 <= x <= H and 1 <= y <= W and grid[x-1][y-1] == '.'

    def can_kick(x, y):
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny):
                return True
        return False

    def bfs(start_x, start_y):
        queue = [(start_x, start_y)]
        visited = set()
        visited.add((start_x, start_y))
        front_kicks = 0

        while queue:
            for _ in range(len(queue)):
                x, y = queue.pop(0)
                if x == C and y == D:
                    return front_kicks
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    nx, ny = x + dx, y + dy
                    if is_valid(nx, ny) and (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append((nx, ny))
                        if can_kick(nx, ny):
                            front_kicks += 1

        return -1

    result = bfs(A, B)
    print(result)

