import sys
from collections import deque

def is_valid(x, y, H, W):
    return 1 <= x <= H and 1 <= y <= W

def bfs(start, end, H, W, grid):
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    queue = deque([(start, 0)])
    visited = set()

    while queue:
        (x, y), steps = queue.popleft()
        if (x, y) == end:
            return steps

        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny, H, W) and grid[nx][ny] == '.' and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append((nx, ny, steps + 1))

    return -1

def main():
    input = sys.stdin.read
    data = input().split()
    H, W = int(data[0]), int(data[1])
    grid = [input().strip() for _ in range(H)]
    start = tuple(map(int, data[2:4]))
    end = tuple(map(int, data[4:6]))

    result = bfs(start, end, H, W, grid)
    print(result)

if __name__ == "__main__":
    main()

