    lines = input().split('\n')
    return int(lines[0]), int(lines[1]), list(map(int, lines[2].split())), int(lines[3]), int(lines[4]), list(map(int, lines[5].split()))

def solve(H, W, A, Q, sh, sw, Ds, As):
    grid = [[0] * W for _ in range(H)]
    grid[sh-1][sw-1] = A[sh-1][sw-1]
    for d, a in zip(Ds, As):
        if d == 'L':
            for i in range(H):
                grid[i][sw-1] = a
        elif d == 'R':
            for i in range(H):
                grid[i][sw] = a
        elif d == 'U':
            for j in range(W):
                grid[sh-1][j] = a
        elif d == 'D':
            for j in range(W):
                grid[sh][j] = a
        sh, sw = sh + 1, sw + 1
    paths = []
    for i in range(1, H + 1):
        for j in range(1, W + 1):
            if (i, j) == (1, 1) or (i, j) == (H, W):
                continue
            paths.append((i, j))
    paths.sort()
    ans = 1
    for path in paths:
        ans *= grid[path[0]-1][path[1]-1]
        ans %= 998244353
    return ans

