def matrix_power_mod(matrix, power, mod):
    result = np.eye(matrix.shape[0], dtype=int)
    base = matrix
    while power > 0:
        if power % 2 == 1:
            result = (result @ base) % mod
        base = (base @ base) % mod
        power //= 2
    return result

def sum_of_matrix_powers_mod(matrix, p, mod):
    N = matrix.shape[0]
    total_sum = np.zeros((N, N), dtype=int)
    for i in range(N):
        for j in range(N):
            if matrix[i, j] == 0:
                B = np.eye(N, dtype=int)
                for _ in range(p - 1):
                    B = (B @ matrix) % mod
                total_sum[i, j] = np.sum(B) % mod
    return total_sum

