import sys

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, M, S, T = map(int, data)
    edges = []
    for _ in range(M):
        u, v = map(int, input().split())
        edges.append((u, v))
    return N, M, S, T, edges

def find_path(N, M, S, T, edges):
    graph = [[] for _ in range(N + 1)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (N + 1)
    path = []
    stack = [S]

    while stack:
        u = stack.pop()
        if not visited[u]:
            visited[u] = True
            path.append(u)
            for v in graph[u]:
                if not visited[v]:
                    stack.append(v)

    if T not in path:
        return -1

    path = path[::-1]
    operations = 0
    for i in range(len(path) - 1):
        if path[i] == S and path[i + 1] == T:
            continue
        for v in graph[path[i]]:
            if v == path[i + 1]:
                operations += 1
                break
    return operations

def main():
    N, M, S, T, edges = read_input()
    result = find_path(N, M, S, T, edges)
    print(result)

