import sys
from collections import defaultdict

def build_graph(N, L, R, W):
    graph = defaultdict(list)
    for i in range(N):
        for j in range(i+1, N):
            if not (L[i] <= L[j] and R[i] >= R[j]):
                graph[i].append(j)
                graph[j].append(i)
    return graph

def find_min_weight_path(graph, s, t, visited):
    if s == t:
        return 0
    visited.add(s)
    min_weight = float('inf')
    for neighbor in graph[s]:
        if neighbor not in visited:
            weight = W[neighbor] + find_min_weight_path(graph, neighbor, t, visited)
            if weight < min_weight:
                min_weight = weight
    visited.remove(s)
    return min_weight if min_weight != float('inf') else -1

def main():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    W = list(map(int, data[1:N+1]))
    L = []
    R = []
    for i in range(N):
        data = input().split()
        L.append(int(data[0]))
        R.append(int(data[1]))
    Q = int(input())
    queries = []
    for i in range(Q):
        data = input().split()
        queries.append((int(data[0]), int(data[1])))
    graph = build_graph(N, L, R, W)
    for s, t in queries:
        print(find_min_weight_path(graph, s-1, t-1, set()))

