    for i in range(1, N + 1):
        if S[i - 1] == '(':
            dp[i] = (dp[i - 1] + dp[i - 2]) % MOD
        else:
            dp[i] = (dp[i - 1] + dp[i - 2]) % MOD
    return dp[N]

