    # Check if there are any negative elements in A or B
    if -1 in A or -1 in B:
        return False

    # Check if the sum of all elements in A and B is zero
    if sum(A) == 0 and sum(B) == 0:
        return True

    # Check if the sum of all elements in A and B is positive
    if sum(A) > 0 and sum(B) > 0:
        return True

    # Check if the sum of all elements in A and B is negative
