def min_operations(A):
    N = len(A)
    operations = 0

    # Count the number of consecutive identical elements
    for i in range(1, N):
        if A[i] == A[i-1]:
            operations += 1
        else:
            break

    # If there are no consecutive identical elements, we can delete all elements
    if operations == 0:
        return N

    # If there are consecutive identical elements, we can delete them in pairs
    return operations // 2

