class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        left, right = 0, 0
        total_operations = 0
        count = 0
        while right < len(nums):
            total_operations += nums[right] - nums[left]
            while total_operations > k:
                total_operations -= nums[left] - nums[left + 1]
                left += 1
            count += right - left + 1
            right += 1
