class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        graph = [[] for _ in range(len(nums))]
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            nonlocal max_length, min_nodes
            if not graph[node]:
                return 0, 1

            max_length = 0
            min_nodes = float('inf')

            for neighbor, length in graph[node]:
                if neighbor == parent:
                    continue
                length, nodes = dfs(neighbor, node)
                if nums[node] != nums[neighbor]:
                    max_length = max(max_length, length + 1)
                    min_nodes = min(min_nodes, nodes + 1)

            if nums[node] == nums[parent]:
                return max_length, min_nodes + 1

            return max_length + 1, min(min_nodes, max_length + 1)

        max_length, min_nodes = dfs(0, -1)
        return [max_length, min_nodes]
