class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        graph = [[] for _ in range(len(nums))]
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            nonlocal longest, second_longest, max_length, min_nodes
            if not graph[node]:
                return 0, 0, 0

            longest = second_longest = 0
            for neighbor, length in graph[node]:
                if neighbor == parent:
                    continue
                longest_child, second_longest_child, max_length_child, min_nodes_child = dfs(neighbor, node)
                if nums[node] != nums[neighbor]:
                    if longest_child > longest:
                        second_longest = longest
                        longest = longest_child
                    elif longest_child > second_longest:
                        second_longest = longest_child
                if max_length_child > max_length:
                    second_longest = longest
                    longest = max_length_child
                    min_nodes = min_nodes_child + 1
                elif max_length_child > second_longest:
                    second_longest = max_length_child
                    min_nodes = min_nodes_child + 1
            if nums[node] == nums[parent]:
                return longest, second_longest, max_length, min_nodes
            if longest > 0:
                return longest + 1, second_longest, max_length + 1, min_nodes + 1
            return 0, 0, 0, 1

        longest, second_longest, max_length, min_nodes = 0, 0, 0, 1
        dfs(0, -1)
        return [max_length, min_nodes]
