class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        adj = [[] for _ in range(len(nums))]
        for u, v, length in edges:
            adj[u].append((v, length))
            adj[v].append((u, length))

        def dfs(node):
            nonlocal max_length, min_nodes
            if node in visited:
                return 0, 0
            visited.add(node)
            children = adj[node]
            children_values = [dfs(child) for child in children]
            children_values.sort(key=lambda x: x[0], reverse=True)
            max_length = max(max_length, children_values[0][0])
            if len(children_values) == 1:
                min_nodes = min(min_nodes, children_values[0][1])
                return children_values[0][0], children_values[0][1]
            if len(children_values) == 2:
                if children_values[0][0] == children_values[1][0]:
                    min_nodes = min(min_nodes, children_values[0][1] + children_values[1][1])
                    return children_values[0][0], children_values[0][1] + children_values[1][1]
                else:
                    max_length = max(max_length, children_values[0][0] + children_values[1][0])
                    return children_values[0][0], children_values[0][1] + children_values[1][1]
            if len(children_values) > 2:
                if children_values[0][0] == children_values[1][0]:
                    min_nodes = min(min_nodes, children_values[0][1] + children_values[1][1] + children_values[2][1])
