        def is_match(i, j):
            if i == len(s) or j == len(p):
                return i == len(s) and j == len(p)
            if p[j] == '*':
                return is_match(i, j + 1) or (i < len(s) and is_match(i + 1, j))
            return i < len(s) and (s[i] == p[j] or p[j] == '?') and is_match(i + 1, j + 1)

        def find_shortest_match(i, j):
            if i == len(s) or j == len(p):
                return float('inf')
            if (i, j) in memo:
                return memo[(i, j)]
            if p[j] == '*':
                memo[(i, j)] = min(find_shortest_match(i, j + 1), find_shortest_match(i + 1, j) + 1)
            elif i < len(s) and (s[i] == p[j] or p[j] == '?'):
                memo[(i, j)] = find_shortest_match(i + 1, j + 1)
            else:
                memo[(i, j)] = float('inf')
            return memo[(i, j)]

        memo = {}
