    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        i, j = 0, 0
        star1, star2 = 0, 0
        while j < len(p):
            if p[j] == '*':
                star1 += 1
                j += 1
            elif p[j] == s[i]:
                i += 1
                j += 1
            elif star1 > 0:
                star1 -= 1
                i += 1
            else:
                return -1
        while star1 > 0:
            star1 -= 1
            i += 1
        while i < len(s):
            if s[i] == p[j]:
                i += 1
                j += 1
            elif j < len(p) and p[j] == '*':
                j += 1
            else:
                return -1
        return len(s) - i
