class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        if len(caption) < 3:
            return caption

        n = len(caption)
        dp = [[0] * n for _ in range(n)]

        for i in range(n):
            dp[i][i] = 1

        for length in range(2, n + 1):
            for i in range(n - length + 1):
                j = i + length - 1
                if caption[i] == caption[j]:
                    dp[i][j] = dp[i + 1][j - 1]
                else:
                    dp[i][j] = min(dp[i + 1][j], dp[i][j - 1]) + 1

        def is_good(i, j):
            if i == j:
                return True
            if i == j - 1:
                return True
            if i == j - 2:
                return True
            if caption[i] == caption[j - 1] and caption[i] == caption[j - 2]:
                return True
            if caption[i] == caption[j] and caption[i] == caption[j - 1]:
                return True
            if caption[i] == caption[j] and caption[i] == caption[j - 2]:
                return True
            return False

        def backtrack(i, j):
            if i == j:
                return [caption[i]]
            if i == j - 1:
                return [caption[i], caption[i]]
            if i == j - 2:
                return [caption[i], caption[i], caption[i + 1]]
            if caption[i] == caption[j - 1] and caption[i] == caption[j - 2]:
                return [caption[i]] + backtrack(i + 1, j - 1) + [caption[j - 1]]
            if caption[i] == caption[j] and caption[i] == caption[j - 1]:
                return [caption[i]] + backtrack(i + 1, j - 1) + [caption[i]]
            if caption[i] == caption[j] and caption[i] == caption[j - 2]:
                return [caption[i]] + backtrack(i + 1, j - 1) + [caption[i]]
            return [caption[i]] + backtrack(i + 1, j - 1) + [caption[j - 1]]

        i, j = 0, n - 1
        while i < j:
            if is_good(i, j):
                return backtrack(i, j)
            if caption[i] == caption[j]:
                i += 1
                j -= 1
            elif caption[i] == caption[j - 1]:
                j -= 1
            elif caption[i + 1] == caption[j]:
                i += 1
            else:
                return ""

        return ""
