class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        n = len(caption)
        if n < 3:
            return ""

        # Initialize the result string with the first three characters
        result = caption[:3]

        # Iterate through the string starting from the fourth character
        for i in range(3, n):
            # Check if the current character is different from the previous one
            if caption[i] != caption[i-1]:
                # Append the next character to the result
                result += chr(ord(caption[i-1]) + 1)
            else:
                # Append the previous character to the result
                result += chr(ord(caption[i-1]) - 1)

        return result
