class Solution:
    def countPartitions(self, nums: List[int]) -> int:
        total_sum = sum(nums)
        if total_sum % 2 != 0:
            return 0

        even_sum = total_sum // 2
        odd_sum = total_sum - even_sum

        dp = [0] * (odd_sum + 1)
        dp[0] = 1

        for num in nums:
            for j in range(odd_sum, num - 1, -1):
                dp[j] += dp[j - num]

        return dp[odd_sum]
