class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        prefix_sum = [0]
        for num in nums:
            prefix_sum.append(prefix_sum[-1] + num)

        prefix_sum_dict = defaultdict(list)
        for i, num in enumerate(prefix_sum):
            prefix_sum_dict[num].append(i)

        ans = float('inf')
        for num in prefix_sum_dict:
            if num % x != 0:
                continue
            target = num // x
            if target < k:
                continue

            for i in prefix_sum_dict[num]:
                if i + x * k > len(prefix_sum) - 1:
                    continue
                if prefix_sum[i + x * k] - prefix_sum[i] < target:
                    continue
                ans = min(ans, prefix_sum[i + x * k] - prefix_sum[i] - target * x)

        return ans

class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        prefix_sum_dict = {0: -1}
        prefix_sum_set = set()
        prefix_sum_set.add(0)
        prefix_sum_dict[prefix_sum[0]] = 0

        for i in range(1, n + 1):
            prefix_sum_set.add(prefix_sum[i])
            prefix_sum_dict[prefix_sum[i]] = i

        prefix_sum_dict = {v: k for k, v in prefix_sum_dict.items()}

        prefix_sum_set = sorted(prefix_sum_set)

        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(1, n + 1):
            dp[i] = min(dp[i], dp[i - 1] + 1)

            for j in range(i - x + 1, i + 1):
                if j - 1 >= 0:
                    dp[i] = min(dp[i], dp[j - 1] + prefix_sum_dict[prefix_sum[i]] - prefix_sum_dict[prefix_sum[j - 1]])

        return dp[n]

class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        prefix_sum_dict = {}
        prefix_sum_dict[0] = -1

        min_operations = float('inf')
        for i in range(n + 1):
            if prefix_sum[i] - x * k in prefix_sum_dict:
                min_operations = min(min_operations, i - prefix_sum_dict[prefix_sum[i] - x * k])
            if prefix_sum[i] - x in prefix_sum_dict:
                prefix_sum_dict[prefix_sum[i] - x] = min(prefix_sum_dict.get(prefix_sum[i] - x, i), i)
            else:
                prefix_sum_dict[prefix_sum[i] - x] = i

