class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        def dfs(node, parent, visited, parent_weight):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent:
                    if not visited[neighbor]:
                        dfs(neighbor, node, visited, max(parent_weight, weight))
                    else:
                        return True
            return False

        def bfs(node, parent, visited, parent_weight):
            queue = deque([(node, parent, parent_weight)])
            while queue:
                node, parent, parent_weight = queue.popleft()
                visited[node] = True
                for neighbor, weight in graph[node]:
                    if neighbor != parent:
                        if not visited[neighbor]:
                            if weight > parent_weight:
                                return True
                            queue.append((neighbor, node, weight))
                        else:
                            return True
            return False

        def dfs_with_threshold(node, parent, visited, parent_weight, threshold):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if neighbor != parent:
                    if not visited[neighbor]:
                        if weight > parent_weight:
                            return True
                        if weight <= threshold:
                            if not dfs_with_threshold(neighbor, node, visited, weight, threshold):
                                return True
                    else:
                        return True
            return False

        def bfs_with_threshold(node, parent, visited, parent_weight, threshold):
            queue = deque([(node, parent, parent_weight)])
            while queue:
                node, parent, parent_weight = queue.popleft()
                visited[node] = True
                for neighbor, weight in graph[node]:
                    if neighbor != parent:
                        if not visited[neighbor]:
                            if weight > parent_weight:
                                return True
                            if weight <= threshold:
                                queue.append((neighbor, node, weight))
                        else:
                            return True
            return False

        visited = [False] * n
