class Solution:
    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return 0

        dp = [0] * (n + 1)
        dp[0] = 0
        for i in range(1, n + 1):
            dp[i] = max(dp[i - 1], dp[max(0, i - m)] + sum(nums[i - m:i]))

        return dp[-1]
