            dp[i][0] = dp[i - 1][0] + nums[i]

        for i in range(1, k):
            dp[0][i] = -float('inf')

        for i in range(1, n):
            for j in range(1, k + 1):
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - 1] + nums[i])

        return max(dp[-1])

