    def minMaxSubarraySum(self, nums: List[int], k: int) -> int:
        def sliding_window(nums, k):
            if k == 0:
                return 0
            if k == len(nums):
                return sum(nums)
            min_val = max_val = nums[0]
            total = nums[0]
            for i in range(1, k):
                if nums[i] < min_val:
                    min_val = nums[i]
                if nums[i] > max_val:
                    max_val = nums[i]
                total += nums[i]
            for i in range(k, len(nums)):
                total += nums[i] - nums[i - k]
                if nums[i] < min_val:
                    min_val = nums[i]
                if nums[i] > max_val:
                    max_val = nums[i]
                if total > max_sum:
                    max_sum = total
            return max_sum

        max_sum = float('-inf')
        for i in range(len(nums) - k + 1):
            max_sum = max(max_sum, sliding_window(nums[i:i + k], k))
        return max_sum
