class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def is_valid(i, j):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]):
                return False
            return grid[i][j] == 1

        def is_valid_diagonal(i, j, direction):
            if direction == 'top-left':
                return is_valid(i, j) and is_valid(i - 1, j + 1)
            elif direction == 'bottom-right':
                return is_valid(i, j) and is_valid(i + 1, j + 1)
            elif direction == 'top-right':
                return is_valid(i, j) and is_valid(i - 1, j - 1)
            elif direction == 'bottom-left':
                return is_valid(i, j) and is_valid(i + 1, j - 1)
            else:
                raise ValueError("Invalid direction")

        def count_diagonal(i, j, direction):
            count = 0
            while is_valid_diagonal(i, j, direction):
                count += 1
                i += 1
                j += 1
            return count

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    for direction in ['top-left', 'bottom-right', 'top-right', 'bottom-left']:
                        length = count_diagonal(i, j, direction)
                        max_length = max(max_length, length)
        return max_length
