class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        def dfs(i, j, direction, sequence):
            if i < 0 or j < 0 or i >= n or j >= m or grid[i][j] != sequence[0]:
                return 0
            sequence.pop(0)
            if not sequence:
                return 1
            if direction == 'top-left':
                return dfs(i-1, j+1, 'top-left', sequence) + dfs(i+1, j-1, 'top-left', sequence)
            elif direction == 'top-right':
                return dfs(i-1, j-1, 'top-right', sequence) + dfs(i+1, j+1, 'top-right', sequence)
            elif direction == 'bottom-left':
                return dfs(i+1, j-1, 'bottom-left', sequence) + dfs(i-1, j+1, 'bottom-left', sequence)
            elif direction == 'bottom-right':
                return dfs(i+1, j+1, 'bottom-right', sequence) + dfs(i-1, j-1, 'bottom-right', sequence)
            else:
                return 0

        max_length = 0
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 1:
                    for sequence in [[2, 0, 2, 0], [0, 2, 0, 2]]:
                        for direction in ['top-left', 'top-right', 'bottom-left', 'bottom-right']:
                            max_length = max(max_length, dfs(i, j, direction, sequence))
        return max_length
