            if x < 0 or x >= len(grid) or y < 0 or y >= len(grid[0]):
                return False
            return grid[x][y] == 1

        def is_valid_diagonal(x, y, dx, dy):
            if not is_valid(x, y):
                return False
            if dx == 0 and dy == 0:
                return False
            if dx == 1 and dy == 1:
                return is_valid(x + 1, y + 1)
            if dx == -1 and dy == 1:
                return is_valid(x - 1, y + 1)
            if dx == 1 and dy == -1:
                return is_valid(x + 1, y - 1)
            if dx == -1 and dy == -1:
                return is_valid(x - 1, y - 1)
            return True

        def dfs(x, y, dx, dy, length):
            if not is_valid_diagonal(x, y, dx, dy):
                return False
            if length > max_length:
                max_length = length
            for dx, dy in [(1, 1), (-1, 1), (1, -1), (-1, -1)]:
                if dfs(x + dx, y + dy, dx, dy, length + 1):
                    return True
            return False

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
