    def minCost(self, n: int, cost: List[List[int]]) -> int:
        @cache
        def dp(i, prev):
            if i == n:
                return 0
            if i == 0:
                return min(cost[i][j] + dp(i + 1, j) for j in range(3))
            if i == n - 1:
                return min(cost[i][j] + dp(i + 1, j) for j in range(3) if j != prev)
            return min(cost[i][j] + dp(i + 1, j) for j in range(3) if j != prev and j != (prev + 1) % 3)
        return dp(0, -1)
