class Solution:
    def minOperations(self, queries: List[List[int]]) -> int:
        res = 0
        for l, r in queries:
            res += self.minOperationsHelper(l, r)
        return res

    def minOperationsHelper(self, l, r):
        if l == r:
            return 0
        if l % 4 == 0:
            return self.minOperationsHelper(l + 1, r)
        if r % 4 == 0:
            return self.minOperationsHelper(l, r - 1)
        if l % 4 == 1:
            return self.minOperationsHelper(l + 2, r) + 1
        if r % 4 == 3:
            return self.minOperationsHelper(l, r - 2) + 1
        return self.minOperationsHelper(l + 1, r - 1) + 2
