class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_dist = [float('inf')] * n
        min_index = [-1] * n
        min_index[0] = 0
        min_dist[0] = 0
        for i in range(1, n):
            if nums[i] == nums[0]:
                min_index[i] = 0
                min_dist[i] = 0
            elif nums[i] == nums[min_index[0]]:
                min_index[i] = min_index[0]
                min_dist[i] = min_dist[0] + 1
            else:
                min_index[i] = min_index[0]
                min_dist[i] = min_dist[0] + 1
        for i in range(1, n):
            if nums[i] == nums[min_index[i]]:
                min_index[i] = i
                min_dist[i] = min_dist[i - 1] + 1
            elif nums[i] == nums[min_index[i - 1]]:
                min_index[i] = min_index[i - 1]
                min_dist[i] = min_dist[i - 1] + 1
            else:
                min_index[i] = min_index[i - 1]
                min_dist[i] = min_dist[i - 1] + 1
        for i in range(n - 2, -1, -1):
            if nums[i] == nums[min_index[i]]:
                min_index[i] = i
                min_dist[i] = min_dist[i + 1] + 1
            elif nums[i] == nums[min_index[i + 1]]:
                min_index[i] = min_index[i + 1]
                min_dist[i] = min_dist[i + 1] + 1
            else:
                min_index[i] = min_index[i + 1]
                min_dist[i] = min_dist[i + 1] + 1
        ans = []
        for i in queries:
            if min_index[i] == -1:
                ans.append(-1)
            else:
                ans.append(min_dist[min_index[i]])
        return ans
