class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_dist = [float('inf')] * n
        min_dist[0] = 0
        for i in range(1, n):
            min_dist[i] = min(min_dist[i - 1] + 1, i)
        for i in range(n - 2, -1, -1):
            min_dist[i] = min(min_dist[i + 1] + 1, n - 1 - i + min_dist[i + 1])
        ans = []
        for i in queries:
            if nums[i] == nums[(i + n - 1) % n]:
                ans.append(-1)
            else:
                ans.append(min_dist[i])
        return ans
