class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_distances = [-1] * n

        for i in range(n):
            if nums[i] == nums[queries[i]]:
                min_distances[i] = 0
                continue

            left = i - 1
            right = (i + 1) % n

            while left != i:
                if nums[left] == nums[queries[i]]:
                    min_distances[queries[i]] = i - left
                    break
                left = (left - 1) % n

            while right != i:
                if nums[right] == nums[queries[i]]:
                    min_distances[queries[i]] = right - i
                    break
                right = (right + 1) % n

        return min_distances
