class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_dist = [float('inf')] * n
        min_index = [-1] * n

        # Find the minimum distance and index for each element
        for i in range(n):
            if nums[i] < min_dist[i]:
                min_dist[i] = nums[i]
                min_index[i] = i
            elif nums[i] == min_dist[i]:
                min_index[i] = i

        # Handle circular array
        for i in range(n):
            if nums[i] == nums[(i + n - 1) % n]:
                min_dist[i] = min_dist[(i + n - 1) % n]
                min_index[i] = min_index[(i + n - 1) % n]

        # Find the minimum distance for each query
        result = []
        for q in queries:
            if min_index[q] != -1:
                result.append(min_dist[q])
            else:
                result.append(-1)

        return result
