class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        n = len(s)
        dp = [[0] * (k + 1) for _ in range(n + 1)]
        for i in range(1, n + 1):
            for j in range(1, k + 1):
                dp[i][j] = dp[i - 1][j]
                if s[i - 1] == 'N':
                    dp[i][j] = max(dp[i][j], dp[i - 1][j - 1] + 1)
                elif s[i - 1] == 'S':
                    dp[i][j] = max(dp[i][j], dp[i - 1][j + 1] + 1)
                elif s[i - 1] == 'E':
                    dp[i][j] = max(dp[i][j], dp[i - 1][j - 1] + 1)
                elif s[i - 1] == 'W':
                    dp[i][j] = max(dp[i][j], dp[i - 1][j + 1] + 1)
        return dp[-1][-1]
