class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        from collections import Counter
        from typing import *
        import heapq

        def max_manhattan_distance(s: str, k: int) -> int:
            """
            Find the maximum Manhattan distance from the origin that can be achieved at any time while performing the movements in order.
            """
            counter = Counter(s)
            max_distance = 0
            current_distance = 0
            for direction, count in counter.items():
                if direction == 'N':
                    current_distance += count
                elif direction == 'S':
                    current_distance -= count
                elif direction == 'E':
                    current_distance += 1
                elif direction == 'W':
                    current_distance -= 1
                if current_distance < 0:
                    current_distance = 0
