    def maxScore(self, points: List[int], m: int) -> int:
        left, right = 0, max(points)
        while left < right:
            mid = (left + right + 1) // 2
            if self.isPossible(points, m, mid):
                left = mid
            else:
                right = mid - 1
        return left

    def isPossible(self, points, m, minScore):
        score = [0] * len(points)
        i = -1
        for _ in range(m):
            if i < 0:
                i = 0
            elif i >= len(points) - 1:
                i = len(points) - 1
            else:
                if points[i] >= minScore:
                    score[i] += points[i]
                    points[i] = 0
                    if i > 0:
                        i -= 1
                    else:
                        i = 0
                elif points[i + 1] >= minScore:
                    score[i + 1] += points[i + 1]
                    points[i + 1] = 0
                    if i < len(points) - 1:
                        i += 1
                    else:
                        i = len(points) - 1
                else:
                    return False
        return True
