class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: x[1])
        n = len(squares)
        left = 0
        right = n - 1
        while left < right:
            mid = (left + right + 1) // 2
            if self.is_valid(squares, mid):
                left = mid
            else:
                right = mid - 1
        return squares[left][1]

    def is_valid(self, squares, mid):
        total_above = 0
        total_below = 0
        for i in range(mid):
            total_above += (squares[i][1] + squares[i][3]) * (squares[i][2] - squares[i][3]) / 2
        for i in range(mid, len(squares)):
            total_below += (squares[i][1] + squares[i][3]) * (squares[i][2] - squares[i][3]) / 2
        return total_above == total_below
