class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: x[1])
        n = len(squares)
        left = 0
        right = n - 1
        while left < right:
            mid = (left + right) // 2
            if self.check(squares, mid):
                right = mid
            else:
                left = mid + 1
        return squares[left][1]

    def check(self, squares, mid):
        total_above = 0
        total_below = 0
        for i in range(mid + 1):
            total_above += (squares[i][1] + squares[i][3]) * squares[i][2]
        for i in range(mid, n):
            total_below += (squares[i][1] + squares[i][3]) * squares[i][2]
        return total_above == total_below

class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: (x[0], x[1]))
        total_area = sum(x[2] ** 2 for x in squares)
        total_area_above = 0
        total_area_below = 0
        y_min = float('inf')
        for x, y, l in squares:
            total_area_above += l ** 2
            total_area_below += (y + l - 1) ** 2
            y_min = min(y_min, y + l - 1)
        return y_min + (total_area - total_area_above) / (total_area_below - total_area_above)

