class Solution:
    def minimumCost(self, nums: List[int], cost: List[int], k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        prefix_cost = [0] * (n + 1)

        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]
            prefix_cost[i + 1] = prefix_cost[i] + cost[i]

        dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]
        dp[0][0] = 0

        for i in range(1, n + 1):
            for j in range(i + 1):
                for l in range(j):
                    dp[i][j] = min(dp[i][j], dp[l][j - l] + (prefix_sum[i] - prefix_sum[l]) * (prefix_cost[i] - prefix_cost[l]) + (prefix_sum[j] - prefix_sum[l]) * (prefix_cost[j] - prefix_cost[l]) + (prefix_sum[i] - prefix_sum[j]) * (prefix_cost[i] - prefix_cost[j]) + (prefix_sum[i] - prefix_sum[j]) * (prefix_cost[i] - prefix_cost[j]) * k)

        return dp[n][n]
