        for i in range(1, n + 1):
            for j in range(i + 1):
                dp[i][j] = min(dp[i - 1][j] + (nums[i - 1] + j * k) * (cost[i - 1] + sum(cost[:i])),
                             dp[i - 1][j - 1] + (nums[i - 1] + (j - 1) * k) * (cost[i - 1] + sum(cost[:i - 1])))

        return dp[n][n]
