class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        nums.sort()
        n = len(nums)
        if n == 0:
            return -1

        def check(subseq):
            if len(subseq) == 0:
                return False
            if len(subseq) % 2 == 0:
                return subseq[0] - subseq[-1] == k
            else:
                return subseq[0] + subseq[-1] == k

        def dfs(i, subseq):
            nonlocal max_product
            if i == n:
                if check(subseq):
                    product = 1
                    for num in subseq:
                        product *= num
                    max_product = max(max_product, product)
                return

            if product * nums[i] > limit:
                return

            dfs(i + 1, subseq + [nums[i]])
            dfs(i + 1, subseq)

        max_product = -1
        dfs(0, [])
        return max_product
