class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        nums.sort()
        n = len(nums)
        if n == 0:
            return -1

        def check(subseq):
            if len(subseq) == 0:
                return False
            if len(subseq) % 2 == 0:
                return subseq[0] - subseq[-1] == k
            else:
                return subseq[0] + subseq[-1] == k

        def backtrack(start, subseq):
            nonlocal best_product
            if sum(subseq) == k and check(subseq):
                best_product = max(best_product, math.prod(subseq))
            if sum(subseq) > k:
                return
            for i in range(start, n):
                if i > start and nums[i] == nums[i-1]:
                    continue
                subseq.append(nums[i])
                backtrack(i+1, subseq)
                subseq.pop()

        best_product = -1
        backtrack(0, [])
        return best_product
