def maxProduct(nums, k, limit):
    def alternating_sum(subseq):
        return sum(subseq[::2]) - sum(subseq[1::2])

    def product(subseq):
        return 1
        # return math.prod(subseq) if all(x > 0 for x in subseq) else -1

    def is_valid(subseq):
        return alternating_sum(subseq) == k and product(subseq) <= limit

    def find_max_product(nums, k, limit):
        max_product = -1
        for r in range(1, len(nums) + 1):
            for subseq in combinations(nums, r):
                if is_valid(subseq):
                    product_value = product(subseq)
                    if product_value > max_product:
                        max_product = product_value
        return max_product

    return find_max_product(nums, k, limit)

