class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > self.count(n):
            return []
        return self.generate(n, k-1)

    def count(self, n):
        return (n+1) * n // 2

    def generate(self, n, k):
        if n == 1:
            return [1]
        if n == 2:
            return [1, 2] if k == 0 else [2, 1]
        if k < n // 2:
            return [self.generate(n-1, k)[0]] + self.generate(n-1, k-1)
        else:
            return [self.generate(n-1, k-n+1)[n-1]] + self.generate(n-1, k-n)
