class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > self.count(n):
            return []
        return self.permute(n, k, [i for i in range(1, n+1)])

    def count(self, n):
        return (n+1) * n // 2

    def permute(self, n, k, nums):
        if n == 1:
            return nums
        if k == 1:
            return nums[:1]
        if k == self.count(n):
            return nums[:n-1]
        if k % 2 == 0:
            return self.permute(n-1, k//2, nums[:n-1])
        else:
            return self.permute(n-1, (k+1)//2, nums[:n-1])
