class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        if k > len(words):
            return [0] * len(words)
        if k == 1:
            return [len(word) for word in words]
        if len(words) == 0:
            return []
        if len(words) == 1:
            return [len(words[0])]
        if len(words) == 2:
            return [len(set(words[0]) & set(words[1]))]

        def common_prefix(a, b):
            i = 0
            while i < min(len(a), len(b)) and a[i] == b[i]:
                i += 1
            return a[:i]

        def find_common_prefixes(words):
            if len(words) == 1:
                return [len(words[0])]
            mid = len(words) // 2
            left = find_common_prefixes(words[:mid])
            right = find_common_prefixes(words[mid:])
            return common_prefix(left[-1], right[-1])

        return find_common_prefixes(words)
