    def lcp(self, words, k):
        if k == 1:
            return len(words[0])
        if not words:
            return 0
        if len(words) < k:
            return 0
        res = 0
        for i in range(len(words[0])):
            if all(words[j][i] == words[0][i] for j in range(1, k)):
                res += 1
            else:
                break
        return res
